import io
from PIL import Image
from django.utils import timezone
from datetime import timedelta
from random import randrange
import lorem
from django.core.files.uploadedfile import SimpleUploadedFile


def generate_random_datetime():
    return timezone.now() - timedelta(hours=randrange(48))


def generate_photo_file(title="test.png"):
    file = io.BytesIO()
    image = Image.new('RGBA', size=(100, 100), color=(155, 0, 0))
    image.save(file, 'png')
    file.name = title
    file.seek(0)
    return file


def generate_any_file(size=None):
    file = io.BytesIO()
    if size is None:
        content = lorem.text()
        file.write(content.encode())
    else:
        file.seek(size)
        file.write(b'0')
    file.seek(0)
    return file


def generate_uploaded_photo_file(title='test.png'):
    return SimpleUploadedFile(title, generate_photo_file().read(), content_type="image/png")


def generate_uploaded_any_file(title='test.txt'):
    return SimpleUploadedFile(
        title, generate_any_file().read(), content_type="text/plain")


def generate_base64_image():
    return "data:image/png;base64,"
